function print(value, eol) {
  if (typeof(eol) == undefined || eol == null) {
    eol = true;
  }
  console.append(toPrettyString(value));
  if (eol) {
    console.line();
  }
}

function printArray(array, itemPrinter, count, vertical, eol) {
  if (typeof(itemPrinter) == undefined || itemPrinter == null) {
    itemPrinter = function(item) {
      print(item, false);
    };
  }
  if (typeof(vertical) == undefined || vertical == null) {
    vertical = true;
  }
  if (typeof(count) == undefined || count == null) {
    count = true;
  }
  if (typeof(eol) == undefined || eol == null) {
    eol = true;
  }
  var length;
  var hasNext;
  var next;
  var i = 0;
  if(array instanceof java.util.Collection) {
    length = array.size();
    iterator = array.iterator();
    hasNext = function() { return iterator.hasNext() ;};
    next = function() { return iterator.next() ;};
  } else if(array instanceof java.util.Iterable) {
    length = -1;
    iterator = array.iterator();
    hasNext = function() { return iterator.hasNext() ;};
    next = function() { return iterator.next() ;};
  } else if(array instanceof java.util.Iterator) {
    length = -1;
    hasNext = function() { return array.hasNext() ;};
    next = function() { return array.next() ;};
  } else {
    length = array.length;
    hasNext = function() { return i < length; };
    next = function() { return array[i]; };
  }
  if (count && length !=-1) {
    console.print('Count: ' + length);
  }
  for(; hasNext(); i++) {
    var item = next()
    if (vertical) {
      itemPrinter(item);
      console.line();
    } else {
      if (i > 0) {
        console.append(', ');
      }
      itemPrinter(item);
    }
  }
  if (!vertical && eol) {
    console.line();
  }
}
function printMatrix(matrix, itemPrinter, count) {
  printArray(matrix, function(array) { printArray(array, itemPrinter, false, false, false); }, count);
}

function jpaNativeQuery(query) {
  var results = entityManager.createNativeQuery(query).resultList;
  var iterator = results.iterator();
  var matches = [];
  while(iterator.hasNext()) {
    var result = iterator.next();
    matches.push(result);
  }
  return matches;
}

function jpaQuery(query) {
  var results = entityManager.createQuery(query).resultList;
  var iterator = results.iterator();
  var matches = [];
  while(iterator.hasNext()) {
    var result = iterator.next();
    matches.push(result);
  }
  return matches;
}

function toPrettyString(value, alwaysReflection) {
  if(typeof(value) == undefined || value == null) {
    return '' + value;
  } else if (value instanceof java.lang.Object) {
    var string;
    if (alwaysReflection) {
      string = new org.apache.commons.lang.builder.ReflectionToStringBuilder(value).toString();
    } else {
      string = value.toString();
      var defaultString = value.getClass().getName() + '@' + java.lang.Integer.toHexString(value.hashCode());
      if(defaultString.equals(string)) {
        string = new org.apache.commons.lang.builder.ReflectionToStringBuilder(value).toString();
      }
    }
    return '' + string;
  } else if (typeof(value) == 'object') {
    if (value.constructor == Array) {
      var string = '[';
      for (var i = 0; i < value.length; i++) {
        if (i > 0) {
          string += ', ';
        }
        string += toString(value[i], alwaysReflection);
      }
      string+= ']';
      return string;
    } else {
      var first = true;
      var string = '{';
      for (var name in value) {
        if (!first) {
          string += ', ';
        }
        first = false;
        string += name + ': ' + toString(value[name], alwaysReflection);
      }
      string+= '}';
      return string;
    }
  } else {
    return '' + value;
  }
}

